(478, 15, '<p>1 і 3 чудово, а в другому проблема, ось правельний код, перепишіть відповідно до своїх значень</p>\r\n<p>var canvas = document.getElementById(\'game\');</p>\r\n<p>var ctx = canvas.getContext(\'2d\');</p>\r\n<p>&nbsp;</p>\r\n<p>window.requestAnimationFrame(enlarge);</p>\r\n<p>&nbsp;</p>\r\n<p>var x = 0;</p>\r\n<p>var z = true;</p>\r\n<p>&nbsp;</p>\r\n<p>function enlarge() {</p>\r\n<p>&nbsp; &nbsp; // clear</p>\r\n<p>&nbsp; &nbsp; clearCanvas();</p>\r\n<p>&nbsp; &nbsp; // start scene</p>\r\n<p>&nbsp; &nbsp; ctx.beginPath();</p>\r\n<p>&nbsp; &nbsp; ctx.arc(canvas.width/2, canvas.height/2, x, 0, 2 * Math.PI);</p>\r\n<p>&nbsp; &nbsp; ctx.stroke();</p>\r\n<p>&nbsp; &nbsp; if (z) {</p>\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; ctx.arc(canvas.width , canvas.height , x++, 0, 2 * Math.PI);</p>\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; if (x &gt;= 150) {</p>\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; z = false;</p>\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; }</p>\r\n<p>&nbsp; &nbsp; }</p>\r\n<p>&nbsp; &nbsp; else {</p>\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; ctx.arc(canvas.width, canvas.height, x--, 0, 2 * Math.PI);</p>\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; if (x &lt;= 0) {</p>\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; z = true;</p>\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; }</p>\r\n<p>&nbsp; &nbsp; }</p>\r\n<p>&nbsp; &nbsp; window.requestAnimationFrame(enlarge);</p>\r\n<p>}</p>\r\n<p>&nbsp;</p>\r\n<p>function clearCanvas() {</p>\r\n<p>&nbsp; &nbsp; ctx.clearRect(0, 0, canvas.width, canvas.height);</p>\r\n<p>&nbsp;</p>\r\n<p>}</p>', '', 0, '2017-05-31 09:15:06', '2017-06-03 05:04:56', 192, 1),
